import './App.css';
import React, { useState} from 'react';
import styled from 'styled-components'; 
import theme from './theme';
import './index.css';
import Nav from './Nav'
import axios from 'axios';


const {fonts} = theme;
const {colors} = theme;
function AdminView(props) {
    
    const Container = styled.div`
        width: 100vw;
        background-color: ${colors.lightBeige};
        height: 80vw;
        display: flex;
        justify-content: center;

    `
    const Preview = styled.div`
        margin-top: 5vw;
        margin-left:3vw;
    `
    const PendingWrapper = styled.div`
        display:flex;
        padding:1vw;
    `
    const values =  axios.get('http://localhost:5000/pendings/');
    console.log(values);
    const pendings = values.data.map((value)=><Pending data = {{
        name:value.name,
        type:value.type,
        subject:value.subject,
        topic:value.topic,
        link:value.link,
        id:value._id
    }}></Pending>)
    return (
        <div>
            <Nav/>
            <Container>
                <Preview>
                    <h3>Pending Notes</h3>
                    <PendingWrapper>
                        {pendings}
                    </PendingWrapper>
                </Preview>
            </Container>

        </div>
    )


}

export default AdminView;
