import './App.css';
import React, { useState} from 'react';
import styled from 'styled-components'; 
import theme from './theme';
import './index.css';
import Nav from './Nav'


const {fonts} = theme;
const {colors} = theme;
function Pending(props) {
    const Pending = styled.div`
        border: solid;
        padding:1vw;
        margin:2vw;
        
    `
    const Button = styled.button`
        
    `
    const Link = styled.a`
        text-decoration:none;
        target="_blank";
    `
    const ButtonWrapper = styled.div`
        display:flex;
    `
    const {name, type,topic, subject, link,id} = props.data;
    
    return (
        <div>
            <Pending>
                <h2>{name}</h2>
                <h3>{type}</h3>
                <h3>{subject + " " + topic}</h3>
                <ButtonWrapper>
                    <Button>Accept</Button>
                    <Button>Deny</Button>
                    <Button ><Link href={link}>Link</Link></Button>
                </ButtonWrapper>
            </Pending>
        </div>
    )


}

export default Pending;
